import React from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "../styles/Style.css";

const styles = {
  heading: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    background: `url("./menu-bg.jpg")`,
    backgroundSize: "100% 100%",
    backgroundRepeat: "no-repeat",
    height: "500px",
  },
  link: {
    textDecoration: "none",
    color: "white",
    fontSize: "3rem",
    textTransform: "uppercase",
    transition: "color 0.3s", // Smooth transition on color change
  },
  // Additional styles for mobile screens
  mobileStyles: {
    heading: {
      height: "300px",
    },
    link: {
      fontSize: "2rem",
    },
  },
};

function Header() {
  const isMobile = window.innerWidth <= 768; // Adjust the breakpoint as needed

  return (
    <div
      style={{
        ...styles.heading,
        ...(isMobile && styles.mobileStyles.heading),
      }}
    >
      <header className="header">
        <h1>
          <Link
            to="/"
            style={{
              ...styles.link,
              ...(isMobile && styles.mobileStyles.link),
            }}
            activeStyle={styles.linkHover}
          >
            Kateryna's Stetsenko Portfolio
          </Link>
        </h1>
        <meta name="viewport" content="width=device-width, initial-scale=1" />
      </header>
    </div>
  );
}

export default Header;
